import React, { useState } from 'react';
import taskcontext from './taskcontext';

const TaskState = (props) => {
  const taskInitial = [
    { "_id": "8272827", "title":"bye","user": "5e6288", "task": "hello" },
    { "_id": "8272826", "title":"bye","user": "5e6288", "task": "hi" },
    { "_id": "8272825", "title":"bye","user": "5e6288", "task": "bedtime" },
    { "_id": "8272821", "title":"bye","user": "5e6288", "task": "hello" },
    { "_id": "8272822", "title":"bye","user": "5e6288", "task": "hello" },
    { "_id": "8272823", "title":"bye","user": "5e6288", "task": "hello" },
    { "_id": "8272824", "title":"bye","user": "5e6288", "task": "hello" },
    { "_id": "8272828", "title":"bye","user": "5e6288", "task": "hello" },
  ];
  const [tasks, setTasks] = useState(taskInitial);

  return (
    <taskcontext.Provider value={{ tasks, setTasks }}>
      {props.children}
    </taskcontext.Provider>
  );
};

// Delete a Note
const deleteNote = (id)=>{
  // TODO: API Call
  console.log("Deleting the note with id" + id);
  const newNotes = notes.filter((note)=>{return note._id!==id})
  setNotes(newNotes)
}
export default TaskState;
