import React, { useContext, useState } from 'react';
import taskContext from './taskcontext';
import Noteitem from './Noteitem';
import './style/note.css';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';

export default function Note({ task }) {
  const { tasks } = useContext(taskContext);
  const [handleAdd, setHandleAdd] = useState(false);

  const updateAdd = () => {
    setHandleAdd(true);
  };

  const closeAddForm = () => {
    setHandleAdd(false);
  };

  return (
    <>
      <div className='container'>
        <div className='row'>
          {tasks.map((task) => (
            <div className="col-md-4" key={task._id}>
              <Noteitem task={task} />
            </div>
          ))}
          <div className="col-md-4">
            <div className="card mb-3" style={{ cursor: 'pointer' }} onClick={updateAdd}>
              <div className="card-body d-flex justify-content-center align-items-center">
                <FontAwesomeIcon icon={faPlus} size="2x" />
              </div>
            </div>
          </div>
        </div>
      </div> 
      {handleAdd && (
        <>
          <div className='overlay' onClick={closeAddForm}></div>
          <div className='addtask-form'>
            <form>
              <div className="mb-3">
                <label htmlFor="title" className="form-label">Title</label>
                <input type="text" className="form-control" id="title" />
              </div>
              <div className="mb-3">
                <label htmlFor="description" className="form-label">Description</label>
                <input type="text" className="form-control" id="description" />
              </div>
              <button type="submit" className="btn btn-primary">Add Task</button>
              <button type="button" className="btn btn-secondary" onClick={closeAddForm}>Cancel</button>
            </form>
          </div>
        </>
      )}
    </>
  );
}
