import React, { useContext } from 'react';
import "./style/noteitem.css";
import taskContext from './taskontext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faSquareCheck } from '@fortawesome/free-solid-svg-icons';

export default function Noteitem({ task }) {
  const { deleteNote } = useContext(taskContext); 
  return (
    <div className="noteitem">
      <span className="title">{task.title}</span>
      <p className="description">{task.description}</p>
      <p className="date">{task.date}</p>
      <div className="actions">
        <FontAwesomeIcon icon={faPenToSquare} onClick={() => deleteNote(task._id)} />
        <FontAwesomeIcon icon={faSquareCheck} />
      </div>
    </div>
  );
}
