import React, { useState, useEffect } from 'react';
import TaskContext from './taskcontext'; // Ensure TaskContext is correctly imported

const TaskState = (props) => {
  const host = "http://localhost:5000";
  const taskInitial = [];
  const [tasks, setTasks] = useState(taskInitial);

  // Fetch all tasks
  const getTasks = async () => {
    const url = `${host}/api/notes/fetchnotes`;http://localhost:5000/api/notes/fetchnotes
    try {
      const response = await fetch(url, {
        headers: {
          'Content-Type': 'application/json',
          'auth-token': localStorage.getItem('token')
        },
      });

      if (!response.ok) {
        throw new Error(`Response status: ${response.status}`);
      }

      const json = await response.json();
      setTasks(json);
    } catch (error) {
      console.error('Failed to fetch tasks:', error.message);
    }
  };

  // Add a new task
  const addTask = async (title, description) => {
    const url = `${host}/api/notes/createnotes`;
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ title, description }),
      });

      if (!response.ok) {
        throw new Error(`Response status: ${response.status}`);
      }

      const newTask = await response.json();
      setTasks([...tasks, newTask]);
    } catch (error) {
      console.error('Failed to add task:', error.message);
    }
  };

  // Update a task
  const updateTask = async (id, title, description) => {
    const url = `${host}/api/notes/updateData/${id}`; // Ensure the URL matches the backend route
    try {
      const response = await fetch(url, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ title, description }),
      });
  
      if (!response.ok) {
        throw new Error(`Response status: ${response.status}`);
      }
  
      const updatedTask = await response.json();
      setTasks(tasks.map((task) => (task._id === id ? updatedTask : task)));
    } catch (error) {
      console.error('Failed to update task:', error.message);
    }
  };

  // Delete a task
  const deleteTask = async (id) => {
    const url = `${host}/api/notes/deleteData/${id}`; // Correct URL
    try {
      const response = await fetch(url, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'auth-token': localStorage.getItem('token')
        },
      });

      if (!response.ok) {
        throw new Error(`Response status: ${response.status}`);
      }

      setTasks(tasks.filter((task) => task._id !== id));
    } catch (error) {
      console.error('Failed to delete task:', error.message);
    }
  };

  // Clear tasks
  const clearTasks = () => {
    setTasks([]);
  };

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getTasks();
    } else {
      clearTasks();
    }
  }, [localStorage.getItem('token')]);

  return (
    <TaskContext.Provider value={{ tasks, setTasks, addTask, updateTask, deleteTask, getTasks, clearTasks }}>
      {props.children}
    </TaskContext.Provider>
  );
};

export default TaskState;
