import React, { useState, useContext } from 'react';
import taskContext from './taskcontext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faSquareCheck } from '@fortawesome/free-solid-svg-icons';
import UpdateForm from './UpdateForm';
import "./style/noteitem.css";

export default function Noteitem({ task }) {
  const { deleteNote } = useContext(taskContext);
  const [showUpdateForm, setShowUpdateForm] = useState(false);

  const handleUpdateClick = () => {
    setShowUpdateForm(true);
  };

  const closeUpdateForm = () => {
    setShowUpdateForm(false);
  };

  return (
    <div>
      <div className={`noteitem ${showUpdateForm ? 'blurred-background' : ''}`}>
        <span className="title">{task.title}</span>
        <p className="description">{task.task}</p>
        <p className="date">{task.date}</p>
        <div className="actions">
          <FontAwesomeIcon icon={faPenToSquare} onClick={handleUpdateClick} />
          <FontAwesomeIcon icon={faSquareCheck} onClick={() => deleteNote(task._id)} />
        </div>
      </div>
      {showUpdateForm && (
        <>
          <div className="overlay"></div>
          <UpdateForm task={task} closeForm={closeUpdateForm} />
        </>
      )}
    </div>
  );
}
