import React, { useState, useEffect } from 'react';
import TaskContext from './taskcontext'; // Ensure TaskContext is correctly imported

const TaskState = (props) => {
  const host = "http://localhost:5000"; 
  const taskInitial = [];
  const [tasks, setTasks] = useState(taskInitial);

  // Fetch all tasks
  const gettasks = async () => {
    const url = `${host}/api/notes/fetchnotes`; // Correct the endpoint
    try {
      const response = await fetch(url, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`Response status: ${response.status}`);
      }

      const json = await response.json();
      setTasks(json); // Update the state with fetched tasks
    } catch (error) {
      console.error('Failed to fetch tasks:', error.message);
    }
  };

  // Delete a task
  const deleteNote = (id) => {
    const updatedTasks = tasks.filter((note) => note._id !== id);
    setTasks(updatedTasks);
  };

  // Update a task
  const updateNote = (id, updatedTask) => {
    const updatedTasks = tasks.map((task) =>
      task._id === id ? { ...task, ...updatedTask } : task
    );
    setTasks(updatedTasks);
  };

  // Add a new task
  const addNote = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  // Fetch tasks when the component mounts
  useEffect(() => {
    gettasks();
  }, []);

  return (
    <TaskContext.Provider value={{ tasks, setTasks, deleteNote, updateNote, addNote, gettasks }}>
      {props.children}
    </TaskContext.Provider>
  );
};

export default TaskState;
