import React from 'react';
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
    const navigate = useNavigate();
    const isLoggedIn = localStorage.getItem('token');
    const userName = localStorage.getItem('username'); // Assuming you store the username in localStorage when logging in

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        navigate('/login');
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <span className='navbar-brand'>MY TASKS</span>
            <div className="collapse navbar-collapse">
                <div className="navbar-nav mr-auto">
                    <Link className="nav-item nav-link" aria-current="page" to="/">Home</Link>
                </div>
                <div className="navbar-nav ml-auto">
                    {!isLoggedIn ? (
                        <div className="btn-group">
                            <button type="button" className="btn btn-primary dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                Login
                            </button>
                            <div className="dropdown-menu">
                                <Link className="dropdown-item" to="/login">Login</Link>
                                <Link className="dropdown-item" to="/signup">Signup</Link>
                            </div>
                        </div>
                    ) : (
                        <>
                            <span className="navbar-text mx-2">Hello, {userName}</span>
                            <button className="btn btn-primary" onClick={handleLogout}>Logout</button>
                        </>
                    )}
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
