import React from 'react';
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
    const navigate = useNavigate();
    const isLoggedIn = localStorage.getItem('token');
    const userName = localStorage.getItem('username'); // Assuming you store the username in localStorage when logging in

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        navigate('/login');
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <div className="container">
                <Link className="navbar-brand" to="/">MY TASKS</Link>
                <div className="collapse navbar-collapse">
                    <ul className="navbar-nav mr-auto">
                        <li className="nav-item">
                            <Link className="nav-link" aria-current="page" to="/">Home</Link>
                        </li>
                    </ul>
                    <ul className="navbar-nav ml-auto">
                        {!isLoggedIn ? (
                            <li className="nav-item dropdown">
                                <button className="btn btn-primary dropdown-toggle" type="button" id="loginDropdown" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    Login
                                </button>
                                <div className="dropdown-menu" aria-labelledby="loginDropdown">
                                    <Link className="dropdown-item" to="/login">Login</Link>
                                    <Link className="dropdown-item" to="/signup">Signup</Link>
                                </div>
                            </li>
                        ) : (
                            <>
                                <li className="nav-item">
                                    <span className="navbar-text mx-2">Hello, {userName}</span>
                                </li>
                                <li className="nav-item">
                                    <button className="btn btn-primary" onClick={handleLogout}>Logout</button>
                                </li>
                            </>
                        )}
                    </ul>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
