import React, { useState, useEffect } from 'react';
import TaskContext from './taskcontext'; // Ensure TaskContext is correctly imported

const TaskState = (props) => {
  const host = "http://localhost:5000";
  const taskInitial = [];
  const [tasks, setTasks] = useState(taskInitial);

  // Fetch all tasks
  const getTasks = async () => {
    const url = `${host}/api/notes/fetchnotes`;
    try {
      const response = await fetch(url, {
        headers: {
          'Content-Type': 'application/json',
          'auth-token': localStorage.getItem('token')
        },
      });

      if (response.status === 401) {
        console.error('Unauthorized. Token may be invalid or expired.');
        // Optionally, clear token and redirect to login page
        localStorage.removeItem('token');
        window.location.href = '/login';
        return;
      }

      if (!response.ok) {
        throw new Error(`Response status: ${response.status}`);
      }

      const json = await response.json();
      setTasks(json);
    } catch (error) {
      console.error('Failed to fetch tasks:', error.message);
    }
  };

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getTasks();
    }
  }, []);

  return (
    <TaskContext.Provider value={{ tasks, setTasks,getTasks }}>
      {props.children}
    </TaskContext.Provider>
  );
};

export default TaskState;
